def mod_get(i, row):
  return row[i % len(row)];

def count_trees(sample, right, down):
  i = 0;
  trees = 0;
  for r in range(0, len(sample), down):
    if mod_get(i, sample[r]) == '#':
      trees += 1;
    i += right;
  return trees;

def star2(slope, inputs):
  product = 1;
  for input in inputs:
    product *= count_trees(slope, input[0], input[1]);
  return product;

